<script>
    function descargarExcel(url) {
        fetch(url)
            .then(response => {
                if (response.ok) {
                    return response.blob();  // Aseg√∫rate de que el archivo sea un blob
                } else {
                    throw new Error('Error al generar el archivo Excel');
                }
            })
            .then(blob => {
                const link = document.createElement('a');
                const urlBlob = window.URL.createObjectURL(blob);
                link.href = urlBlob;
                link.download = 'Reporte.csv'; // Establecer nombre del archivo
                link.click();
                window.URL.revokeObjectURL(urlBlob);
            })
            .catch(error => {
                console.error(error);
            });
        }

    document.getElementById('descargarExcel').addEventListener('click', function() {
        descargarExcel('?action=generarExcel');  // Llamar al controlador para generar Excel
    });

</script>
<script>
    document.getElementById("responsable_id").addEventListener("change", async  function() {
    try {
      const selectElement = document.getElementById('responsable_id');
      const id_user = selectElement.value;
      const response = await fetch(`ajax/ejes-by-usuarios.ajax.php?id=${id_user}`);
      const data = await response.json();
      const ejeSelect = document.getElementById('ejes_intervienen_ids');
      ejeSelect.innerHTML = '<option value="">Seleccionar eje</option>';
      data.forEach(item => {
          const option = document.createElement('option');
          option.value = item.id; 
          option.textContent = item.eje; 
          ejeSelect.appendChild(option);
      });
    } catch (error) {
       console.error('Error fetching eje:', error);
    }
  });
</script>
<script>

    function verFiltros(grafica) {
        if(grafica==1){
            document.getElementById('responsablesTareas').style.display = "none";
            document.getElementById('grafica').style.display = "block";
        }
        if(grafica==2){
            document.getElementById('grafica').style.display = "none";
            document.getElementById('responsablesTareas').style.display = "block";
        }
   
    }

    const ctx = document.getElementById('graficaBarrarNivel').getContext('2d');
  
    let labels = ['Atrasadas', 'En Proceso', 'Terminadas'];
    let values= [0, 0, 0]; 

    const chart = new Chart(ctx, {
        type: 'bar',
        data: {
            labels: labels,
            datasets: [{
                label: 'Avance (%)',
                data: values,
                backgroundColor: [
                    '#db1919', 
                    '#f5e348', 
                    '#54ff76', 
                ],
                borderColor: [
                    'rgba(219, 25, 25, 0.2)', 
                    'rgba(245, 227, 72, 0.37)', 
                    'rgba(84, 255, 118, 0.46)', 
                ],
                borderWidth: 1 
            }]
        },
        options: {
            indexAxis: 'y', 
            responsive: true,
            scales: {
                x: { 
                    beginAtZero: true,
                    max: 100, 
                    ticks: {
                        stepSize: 10 
                    },
                    title: {
                        display: true,
                        text: 'Porcentaje (%)'
                    }
                },
                y: { 
                    title: {
                        display: true,
                        text: 'Estados'
                    }
                }
            }
        }
    });


    async function nivelesAcances() {
    try {
        const selectElement = document.getElementById('ejes_intervienen_id');
        const btn = document.getElementById('btnExcel');
        const btnPDFs = document.getElementById('exportarPDF');
        btn.disabled = false;
        btnPDFs.disabled = false;
        
        const id_eje = selectElement.value;
        document.getElementById('id_eje').value = id_eje;

        const response = await fetch(`ajax/grafica-nivel-avance.ajax.php?id_eje=${id_eje}`);
        const data = await response.json();

        values = [
            parseFloat(data.porcentaje_atrasadas),  
            parseFloat(data.porcentaje_en_proceso),  
            parseFloat(data.porcentaje_terminadas)   
        ];

        chart.data.datasets[0].data = values;
        console.log(data);
        chart.update();
        console.log(data);
        document.querySelector('#pill1').textContent = data.atrasadas;
        document.querySelector('#pill2').textContent = data.en_proceso;
        document.querySelector('#pill3').textContent = data.terminadas;

        
        
        const tbody = document.querySelector("#tabla-avances tbody");
        let filas = "";
        tbody.innerHTML = filas;

    } catch (error) {
        console.error('Error al cargar datos:', error);
    }
}
</script>
<script>
    document.getElementById('exportarPDF').addEventListener('click', function () {
        document.getElementById('headerPDF').style.display = "flex";
        generarPDF();
    });

    function generarPDF() {
        const graficaDiv = document.getElementById('grafica');
        html2canvas(graficaDiv).then(canvas => {
            const imgData = canvas.toDataURL('image/png');
            const pdf = new jspdf.jsPDF();
            const imgWidth = 190;
            const pageHeight = pdf.internal.pageSize.height;
            const imgHeight = (canvas.height * imgWidth) / canvas.width;

            let position = 0;
            if (imgHeight > pageHeight) {
                while (position < imgHeight) {
                    pdf.addImage(imgData, 'PNG', 10, position, imgWidth, pageHeight);
                    position += pageHeight;
                    if (position < imgHeight) {
                        pdf.addPage();
                    }
                }
            } else {
                pdf.addImage(imgData, 'PNG', 10, 10, imgWidth, imgHeight);
            }

            pdf.save('Reporte-Nivel-avance.pdf');
        });
    }
</script>
